package main

import "fmt"

type PathChecker struct {
	Grid   [][]int
	MaxX   int
	MaxY   int
	Routes int
}

func NewPathChecker(grid [][]int) *PathChecker {
	maxX := len(grid) - 1
	maxY := len(grid[0]) - 1
	return &PathChecker{Grid: grid, MaxX: maxX, MaxY: maxY, Routes: 0}
}

func (p *PathChecker) CheckStep(x, y int) {
	if x == p.MaxX && y == p.MaxY && p.Grid[x][y] == 0 {
		p.Routes++
	}
	if x <= p.MaxX && y <= p.MaxY && p.Grid[x][y] == 0 {
		p.CheckStep(x+1, y)
		p.CheckStep(x, y+1)
	}
}

func uniquePathsWithObstaclesRec(obstacleGrid [][]int) int {
	prt(obstacleGrid)
	pathChecker := NewPathChecker(obstacleGrid)
	pathChecker.CheckStep(0, 0)
	return pathChecker.Routes
}

func uniquePathsWithObstaclesDp(obstacleGrid [][]int) int {
	maxX, maxY := len(obstacleGrid), len(obstacleGrid[0])
	obstacleGrid[0][0] = -1
	for x := 0; x < maxX; x++ {
		for y := 0; y < maxY; y++ {
			if obstacleGrid[x][y] == 1 {
				continue
			}
			if x < maxX-1 && obstacleGrid[x+1][y] != 1 {
				obstacleGrid[x+1][y] += obstacleGrid[x][y]
			}
			if y < maxY-1 && obstacleGrid[x][y+1] != 1 {
				obstacleGrid[x][y+1] += obstacleGrid[x][y]
			}
		}
	}
	prt(obstacleGrid)
	return -1 * obstacleGrid[maxX-1][maxY-1]
}

